import React, { Component } from 'react';
import { Paper, Typography } from "@material-ui/core";

export default function Job({ job, i }) {

    const date = job.created_at;
    let month, day, year;

    month = date.split(' ')[1];
    day = date.split(' ')[2];
    year = date.split(' ')[5];

    const monthShortNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
        "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

    const indexM = monthShortNames.findIndex(m => m === month);
    // month = index + 1;

    // let d = new Date();
    // const date1 = new Date(d.getMonth() + 1 + '/' + d.getDate() + '/' + d.getFullYear());
    // const date2 = new Date(month + '/' + day + '/' + year);
    // const diffTime = Math.abs(date1 - date2);
    // const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    const d = new Date();
    const Date1 = new Date(d.getFullYear(), d.getMonth(), d.getDate());
    const Date2 = new Date(year, indexM, day);
    const diffDays = Math.floor((Date2.getTime() - Date.getTime()) / (1000 * 60 * 60 * 24));

    job.diffDays = diffDays;

    return (

        <div>
            <Typography className="counter" variant="h5">{i + 1}</Typography>
            <Paper className="job">
                <div>
                    <Typography variant="h5">{job.title}</Typography>
                    <Typography variant="h6">{job.company}</Typography>
                    <Typography>{job.location}</Typography>
                </div>
                <div>
                    <img style={{ height: '70x', width: '70px' }} src={job.company_logo}></img>
                    <Typography style={{ fontSize: '12px' }} className="postedDay">
                        {job.diffDays} Days Ago
                    </Typography>
                </div>
                {/* {job.company}
    {job.location} */}
            </Paper >
        </div>
    )
}
