import React from 'react';
import './App.css';
import Jobs from "../src/Components/Jobs";

let mockJobs = [];

export default class App extends React.Component {

  state = {
    mockJobs: [],
  }

  async componentDidMount() {

    let d = new Date();
    const date1 = new Date(d.getMonth() + 1 + '/' + d.getDate() + '/' + d.getFullYear());
    const date2 = new Date(1 + '/' + 30 + '/' + 2019);
    const diffTime = Math.abs(date1 - date2);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    console.log(diffDays)
    this.handleLoad();
  }

  handleLoad = async () => {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://jobs.github.com/positions.json"; // site that doesn’t send Access-Control-*
    fetch(proxyurl + url) // https://cors-anywhere.herokuapp.com/https://example.com
      .then(response => response.json())
      .then(contents => {
        mockJobs = Object.keys(contents).map(function (key) {
          return contents[key];
        });
        console.log(mockJobs)

        this.setState({
          mockJobs: mockJobs.sort,
        });
      });

  }

  render() {
    return (
      <div className="App">
        <Jobs jobs={mockJobs} />
      </div>
    )
  }
}
