import React from 'react';
import './App.css';
import Jobs from "../src/Components/Jobs";

let mockJobs = [];

export default class App extends React.Component {

  state = {
    mockJobs: [],
  }

  async componentDidMount() {

    this.handleLoad();
    let d = new Date();

    console.log(d.getFullYear() + '/' + d.getMonth() + 1 + '/' + d.getDate());
  }

  handleLoad = async () => {
    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = "https://jobs.github.com/positions.json"; // site that doesn’t send Access-Control-*
    fetch(proxyurl + url) // https://cors-anywhere.herokuapp.com/https://example.com
      .then(response => response.json())
      .then(contents => {
        mockJobs = Object.keys(contents).map(function (key) {
          return contents[key];
        });
        this.setState({
          mockJobs: mockJobs,
        });
        const date = mockJobs[0].created_at;
        let month, day, year;

        const monthShortNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
          "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"
        ];

        month = date.split(' ')[1];
        day = date.split(' ')[2];
        year = date.split(' ')[5];
        const index = monthShortNames.findIndex(month => month === 'Jan');

        console.log(index + 1); // 3
        console.log(monthShortNames[index]); // blueberries

      });
  }

  render() {
    return (
      <div className="App">
        <Jobs jobs={mockJobs} />
      </div>
    )
  }
}
