import React, { Component } from 'react';
import { Paper, Typography } from "@material-ui/core";

export default function Job({ job }) {

    const date = job.created_at;
    let month, day, year;
    
    month = date.split(' ')[1];
    day = date.split(' ')[2];
    year = date.split(' ')[5];

    const monthShortNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
      "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

    const index = monthShortNames.findIndex(month => month === 'Jan');
    month = index + 1;

    let d = new Date();
    const date1 = new Date(d.getMonth() + 1 + '/' + d.getDate() + '/' + d.getFullYear());
    const date2 = new Date(month + '/' + day + '/' + year);
    const diffTime = Math.abs(date2 - date1);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    job.diffDays = diffDays;

    return (
        <Paper className="job">
            <div>
                <Typography variant="h5">{job.title}</Typography>
                <Typography variant="h6">{job.company}</Typography>
                <Typography>{job.location}</Typography>
            </div>
            <div>
                <Typography variant="h6" className="job-location">
                    {job.diffDays}
                </Typography>
            </div>
            {/* {job.company}
            {job.location} */}
        </Paper >
    )
}
