import React from 'react';
import { Paper, Typography } from "@material-ui/core";

export default function Job({ job, i }) {

    const date = job.created_at;
    let month, day, year;

    month = date.split(' ')[1];
    day = date.split(' ')[2];
    year = date.split(' ')[5];

    const monthShortNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
        "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];

        // find posted day
    const indexM = monthShortNames.findIndex(m => m === month);
    const d = new Date();
    const Date1 = new Date(d.getFullYear(), d.getMonth(), d.getDate());
    const Date2 = new Date(year, indexM, day);
    const diffDays = Math.floor((Date1.getTime() - Date2.getTime()) / (1000 * 60 * 60 * 24));

    job.diffDays = diffDays;

    return (

        <div>
            <Typography className="counter" variant="h6">{i + 1}</Typography>
            <Paper style={{backgroundColor:'#f1f1f1'}} className="job">
                <div>
                    <Typography variant="h5">{job.title}</Typography>
                    <Typography variant="h6">{job.company}</Typography>
                    <Typography>{job.location}</Typography>
                </div>
                <div>
                    <img style={{ height: '60x', width: '60px' }} src={job.company_logo}></img>
                    <Typography style={{ fontSize: '12px' }} className="postedDay">
                        {job.diffDays > 1 ? job.diffDays + ' Days Ago Posted' : 'Today Posted'}
                    </Typography>
                </div>
                {/* {job.company}
    {job.location} */}
            </Paper >
        </div>
    )
}
